export const ChevronDownIcon = ({ rotated, left, right }: { rotated?: boolean, left?: boolean, right?: boolean }) => {
    // This rotation logic is correct and handles all cases
    let transform = 'rotate(0deg)'; // Default (down)
    if (rotated) {
        transform = 'rotate(180deg)'; // Up
    } else if (left) {
        transform = 'rotate(90deg)';  // Left
    } else if (right) {
        transform = 'rotate(-90deg)'; // Right
    }

    return (
        // This is the new SVG code you provided
        <svg
            width="16"
            height="16"
            viewBox="0 0 16 16"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
            style={{ transform, transition: 'transform 0.2s ease-in-out' }}
        >
            <path d="M2.72125 5.9949L7.06792 10.3416C7.58125 10.8549 8.42125 10.8549 8.93458 10.3416L13.2813 5.9949" stroke="#292D32" strokeMiterlimit="10" strokeLinecap="round" strokeLinejoin="round" />
        </svg>
    );
};


